import { useState, useEffect } from 'react';

export const useAuth = () => {
    const [token, setToken] = useState(localStorage.getItem('token'));

    useEffect(() => {
        localStorage.setItem('token', token);
    }, [token]);

    const hookLogin = (token) => {
        setToken(token);
    }
        const hookLogout = () => {
            setToken(null);
        }
        return { token, hookLogin, hookLogout };

}
