export const Login = () => {
    // const history = useHistory();
    const form = {
        username: "",
        password: "",
        firstName: "",
        lastName: "",
        email: ""
    };
    const [signup, setSignup] = useState(form);
    async function handleSubmit(e) {
        e.preventDefault();
        // if (form.type === "drinks") {
        //     await SnackOrBoozeApi.addDrink({
        //          form.name,
        //         name: form.name,
