import React, { useState, useEffect } from "react";
import { JoblyApi } from "./Api";
// import {useHistory} from "react-router-dom"



export const Signup = ({register}) => {

    const [token, setToken] = useState();
    useEffect(() => {
        async function getToken() {
            const getToken = await JoblyApi.login();
            setToken(getToken);
    }
        getToken();

   }, [])

    // const history = useHistory();
        const form = {
        username: "",
        password: "",
        firstName: "",
        lastName: "",
        email: ""
        };


    const [signup, setSignup] = useState(form);
    async function handleSubmit(e) {
        e.preventDefault();
        register(signup.username, signup.password, signup.firstName, sig)
        // if (form.type === "drinks") {
        //     await SnackOrBoozeApi.addDrink({
        //          form.name,
        //         name: form.name,
        //         description: form.description,
        //         recipe: form.recipe,
        //         serve: form.serve
        //     });

        //  history.push(`/${form.type}`);
        // setNewForm(initialState);
        }







    const handleChange = (e) => {
        const { name, value } = e.target;
        setSignup({
            [name]: value
        });

        console.log(signup);

    };



    return (
        <>
            <h1>Signup</h1>
            { console.log(token)}
            <form onSubmit={handleSubmit}>
                {

                }
                <label htmlFor="username"><b>Username</b></label>  
                <input
                    name="username"
                    type="text"
                    onChange={handleChange}
                    value={form.username}
                />

                <label htmlFor="password"><b>Password</b></label> 
                <input
                    name="password"
                    type="text"
                    onChange={handleChange}
                    value={form.password}
                />

                <label htmlFor="firstname"><b>First name</b></label> 
                <input
                    name="firstname"
                    type="text"
                    onChange={handleChange}
                    value={form.firstName}
                />

                <label htmlFor="lastname"><b>Last name</b></label> 
                <input
                    name="lastname"
                    type="text"
                    onChange={handleChange}
                    value={form.lastName}
                />

                <label htmlFor="email"><b>Email</b></label> 
                <input
                    name="email"
                    type="text"
                    onChange={handleChange}
                    value={form.email}
                />

                <br/>
                <button> Submit </button>
            </form>


        </>
    )
            }
